import React, { useState, useEffect } from 'react';
import { ref, onValue } from 'firebase/database';
import { database } from '../firebase/config';
import { useAuth } from '../contexts/SimpleAuthContext';
import { 
  TrendingUp, 
  TrendingDown, 
  Calendar, 
  Target, 
  Award, 
  Activity,
  Clock,
  Zap,
  BarChart3,
  Users
} from 'lucide-react';
import { Line, Doughnut } from 'react-chartjs-2';
import { format, subDays, isAfter } from 'date-fns';

const Dashboard = ({ setActiveTab }) => {
  const { getUserDataPath } = useAuth();
  const [workouts, setWorkouts] = useState([]);
  const [metrics, setMetrics] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const workoutsPath = getUserDataPath('workouts');
    const metricsPath = getUserDataPath('bodyMetrics');
    
    if (!workoutsPath || !metricsPath) {
      setLoading(false);
      return;
    }

    // For localStorage-based data
    if (currentUser) {
      try {
        // Load workouts from localStorage
        const savedWorkouts = localStorage.getItem(`gymtracker_workouts_${currentUser.uid}`);
        if (savedWorkouts) {
          const workoutsData = JSON.parse(savedWorkouts);
          const workoutsList = Object.keys(workoutsData).map(key => ({
            id: key,
            ...workoutsData[key]
          })).sort((a, b) => new Date(b.date) - new Date(a.date));
          setWorkouts(workoutsList);
        }

        // Load metrics from localStorage
        const savedMetrics = localStorage.getItem(`gymtracker_metrics_${currentUser.uid}`);
        if (savedMetrics) {
          const metricsData = JSON.parse(savedMetrics);
          const metricsList = Object.keys(metricsData).map(key => ({
            id: key,
            ...metricsData[key]
          })).sort((a, b) => new Date(b.date) - new Date(a.date));
          setMetrics(metricsList);
        }
      } catch (error) {
        console.error('Error loading data from localStorage:', error);
      }
      setLoading(false);
    }
  }, [getUserDataPath, currentUser]);

  // Calculate stats
  const getStats = () => {
    const last7Days = subDays(new Date(), 7);
    const last30Days = subDays(new Date(), 30);
    
    const recentWorkouts = workouts.filter(w => isAfter(new Date(w.date), last7Days));
    const monthlyWorkouts = workouts.filter(w => isAfter(new Date(w.date), last30Days));
    
    const totalVolume = workouts.reduce((sum, w) => sum + (w.totalVolume || 0), 0);
    const avgVolume = workouts.length > 0 ? Math.round(totalVolume / workouts.length) : 0;
    
    const currentWeight = metrics.find(m => m.weight)?.weight || 0;
    const previousWeight = metrics.length > 1 ? metrics[1]?.weight || currentWeight : currentWeight;
    const weightChange = currentWeight - previousWeight;
    
    return {
      totalWorkouts: workouts.length,
      weeklyWorkouts: recentWorkouts.length,
      monthlyWorkouts: monthlyWorkouts.length,
      totalVolume,
      avgVolume,
      currentWeight,
      weightChange,
      streak: calculateStreak()
    };
  };

  const calculateStreak = () => {
    if (workouts.length === 0) return 0;
    
    let streak = 0;
    let currentDate = new Date();
    
    for (let i = 0; i < workouts.length; i++) {
      const workoutDate = new Date(workouts[i].date);
      const daysDiff = Math.floor((currentDate - workoutDate) / (1000 * 60 * 60 * 24));
      
      if (daysDiff <= streak + 1) {
        streak++;
        currentDate = workoutDate;
      } else {
        break;
      }
    }
    
    return streak;
  };

  // Get recent activity data for chart
  const getActivityData = () => {
    const last14Days = Array.from({ length: 14 }, (_, i) => {
      const date = subDays(new Date(), 13 - i);
      const workoutsOnDate = workouts.filter(w => 
        format(new Date(w.date), 'yyyy-MM-dd') === format(date, 'yyyy-MM-dd')
      );
      
      return {
        date: format(date, 'MMM dd'),
        workouts: workoutsOnDate.length,
        volume: workoutsOnDate.reduce((sum, w) => sum + (w.totalVolume || 0), 0)
      };
    });

    return {
      labels: last14Days.map(d => d.date),
      datasets: [
        {
          label: 'Volume',
          data: last14Days.map(d => d.volume),
          borderColor: 'rgb(59, 130, 246)',
          backgroundColor: 'rgba(59, 130, 246, 0.1)',
          tension: 0.4,
          fill: true,
        }
      ]
    };
  };

  // Get muscle group distribution
  const getMuscleGroupData = () => {
    const muscleGroups = {};
    
    workouts.forEach(workout => {
      if (workout.exercises) {
        workout.exercises.forEach(exercise => {
          const group = exercise.muscleGroup || 'Other';
          muscleGroups[group] = (muscleGroups[group] || 0) + 1;
        });
      }
    });

    const colors = [
      '#3B82F6', '#10B981', '#F59E0B', '#EF4444', 
      '#8B5CF6', '#F97316', '#06B6D4', '#84CC16'
    ];

    return {
      labels: Object.keys(muscleGroups),
      datasets: [
        {
          data: Object.values(muscleGroups),
          backgroundColor: colors.slice(0, Object.keys(muscleGroups).length),
          borderWidth: 0,
        }
      ]
    };
  };

  const stats = getStats();

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false,
      },
    },
    scales: {
      x: {
        display: false,
      },
      y: {
        display: false,
      }
    },
    elements: {
      point: {
        radius: 0,
      }
    }
  };

  const doughnutOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom',
        labels: {
          color: '#94a3b8',
          padding: 20,
          usePointStyle: true,
        }
      },
    },
    cutout: '70%',
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto p-6 space-y-8 animate-fade-in">
      {/* Header */}
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold gradient-text mb-2">Welcome Back!</h1>
        <p className="text-dark-400 text-lg">Here's your fitness journey overview</p>
      </div>

      {/* Quick Stats Grid */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
        <div className="stat-card">
          <div className="flex items-center justify-between mb-2">
            <Calendar className="text-primary-500" size={24} />
            <span className="text-2xl font-bold text-primary-400">{stats.weeklyWorkouts}</span>
          </div>
          <p className="text-dark-400 text-sm">This Week</p>
        </div>

        <div className="stat-card">
          <div className="flex items-center justify-between mb-2">
            <Zap className="text-secondary-500" size={24} />
            <span className="text-2xl font-bold text-secondary-400">{stats.streak}</span>
          </div>
          <p className="text-dark-400 text-sm">Day Streak</p>
        </div>

        <div className="stat-card">
          <div className="flex items-center justify-between mb-2">
            <Target className="text-accent-500" size={24} />
            <span className="text-2xl font-bold text-accent-400">{stats.avgVolume}</span>
          </div>
          <p className="text-dark-400 text-sm">Avg Volume</p>
        </div>

        <div className="stat-card">
          <div className="flex items-center justify-between mb-2">
            <Award className="text-purple-500" size={24} />
            <div className="flex items-center gap-1">
              <span className="text-2xl font-bold text-purple-400">{stats.currentWeight}</span>
              {stats.weightChange !== 0 && (
                stats.weightChange > 0 ? 
                  <TrendingUp className="text-red-400" size={16} /> : 
                  <TrendingDown className="text-green-400" size={16} />
              )}
            </div>
          </div>
          <p className="text-dark-400 text-sm">Current Weight</p>
        </div>
      </div>

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Activity Chart */}
        <div className="lg:col-span-2 card-glass p-6">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-xl font-semibold flex items-center gap-2">
              <Activity className="text-primary-500" size={24} />
              Recent Activity
            </h3>
            <span className="text-sm text-dark-400">Last 14 days</span>
          </div>
          <div className="chart-container">
            <Line data={getActivityData()} options={chartOptions} />
          </div>
        </div>

        {/* Muscle Group Distribution */}
        <div className="card-glass p-6">
          <h3 className="text-xl font-semibold mb-6 flex items-center gap-2">
            <BarChart3 className="text-secondary-500" size={24} />
            Focus Areas
          </h3>
          <div className="chart-container">
            <Doughnut data={getMuscleGroupData()} options={doughnutOptions} />
          </div>
        </div>
      </div>

      {/* Recent Workouts & Achievements */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Recent Workouts */}
        <div className="card-glass p-6">
          <h3 className="text-xl font-semibold mb-6 flex items-center gap-2">
            <Clock className="text-accent-500" size={24} />
            Recent Workouts
          </h3>
          <div className="space-y-4">
            {workouts.slice(0, 5).map((workout, index) => (
              <div key={workout.id} className="flex items-center justify-between p-4 bg-dark-800/50 rounded-lg hover:bg-dark-700/50 transition-colors">
                <div>
                  <h4 className="font-medium text-dark-100">{workout.name}</h4>
                  <p className="text-sm text-dark-400">
                    {format(new Date(workout.date), 'MMM dd, yyyy')} â€¢ {workout.exercises?.length || 0} exercises
                  </p>
                </div>
                <div className="text-right">
                  <div className="text-lg font-semibold text-primary-400">{workout.totalVolume || 0}</div>
                  <div className="text-xs text-dark-400">volume</div>
                </div>
              </div>
            ))}
            {workouts.length === 0 && (
              <div className="text-center py-8 text-dark-400">
                <Activity size={48} className="mx-auto mb-4 opacity-50" />
                <p>No workouts logged yet</p>
                <p className="text-sm">Start your fitness journey today!</p>
              </div>
            )}
          </div>
        </div>

        {/* Quick Actions & Goals */}
        <div className="card-glass p-6">
          <h3 className="text-xl font-semibold mb-6 flex items-center gap-2">
            <Target className="text-purple-500" size={24} />
            Quick Actions
          </h3>
          <div className="space-y-4">
            <button 
              onClick={() => setActiveTab('logger')}
              className="w-full btn-primary flex items-center justify-center gap-2"
            >
              <Calendar size={20} />
              Log Today's Workout
            </button>
            <button 
              onClick={() => setActiveTab('metrics')}
              className="w-full btn-secondary flex items-center justify-center gap-2"
            >
              <Users size={20} />
              Update Body Metrics
            </button>
            <button 
              onClick={() => setActiveTab('charts')}
              className="w-full btn-accent flex items-center justify-center gap-2"
            >
              <BarChart3 size={20} />
              View Full Analytics
            </button>
          </div>

          {/* Weekly Goal Progress */}
          <div className="mt-8 p-4 bg-dark-800/50 rounded-lg">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm font-medium">Weekly Goal</span>
              <span className="text-sm text-dark-400">{stats.weeklyWorkouts}/4</span>
            </div>
            <div className="w-full bg-dark-700 rounded-full h-2">
              <div 
                className="bg-gradient-to-r from-primary-500 to-primary-600 h-2 rounded-full transition-all duration-500"
                style={{ width: `${Math.min((stats.weeklyWorkouts / 4) * 100, 100)}%` }}
              ></div>
            </div>
            <p className="text-xs text-dark-400 mt-2">
              {stats.weeklyWorkouts >= 4 ? 'ðŸŽ‰ Goal achieved!' : `${4 - stats.weeklyWorkouts} more to go!`}
            </p>
          </div>
        </div>
      </div>

      {/* Monthly Summary */}
      <div className="card-glass p-6">
        <h3 className="text-xl font-semibold mb-6 flex items-center gap-2">
          <BarChart3 className="text-primary-500" size={24} />
          Monthly Summary
        </h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
          <div className="text-center">
            <div className="text-3xl font-bold text-primary-400 mb-1">{stats.monthlyWorkouts}</div>
            <div className="text-sm text-dark-400">Workouts</div>
          </div>
          <div className="text-center">
            <div className="text-3xl font-bold text-secondary-400 mb-1">{Math.round(stats.totalVolume / 1000)}K</div>
            <div className="text-sm text-dark-400">Total Volume</div>
          </div>
          <div className="text-center">
            <div className="text-3xl font-bold text-accent-400 mb-1">
              {workouts.reduce((sum, w) => sum + (w.exercises?.length || 0), 0)}
            </div>
            <div className="text-sm text-dark-400">Exercises</div>
          </div>
          <div className="text-center">
            <div className="text-3xl font-bold text-purple-400 mb-1">
              {Math.round(stats.monthlyWorkouts > 0 ? stats.totalVolume / stats.monthlyWorkouts : 0)}
            </div>
            <div className="text-sm text-dark-400">Avg/Workout</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
