import React from 'react';
import { 
  PlusCircle, 
  BarChart3, 
  User, 
  Trophy, 
  Menu,
  X
} from 'lucide-react';

const Navigation = ({ activeTab, setActiveTab, isMobileMenuOpen, setIsMobileMenuOpen }) => {
  const navItems = [
    { id: 'logger', label: 'Log Workout', icon: PlusCircle },
    { id: 'charts', label: 'Progress', icon: BarChart3 },
    { id: 'metrics', label: 'Body Metrics', icon: User },
    { id: 'records', label: 'Personal Records', icon: Trophy },
  ];

  return (
    <>
      {/* Mobile Menu Button */}
      <div className="md:hidden fixed top-4 left-4 z-50">
        <button
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          className="bg-gray-800 p-2 rounded-lg text-white"
        >
          {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>

      {/* Mobile Menu Overlay */}
      {isMobileMenuOpen && (
        <div 
          className="md:hidden fixed inset-0 bg-black bg-opacity-50 z-40"
          onClick={() => setIsMobileMenuOpen(false)}
        />
      )}

      {/* Navigation */}
      <nav className={`
        fixed md:static top-0 left-0 h-full md:h-auto w-64 md:w-full
        bg-gray-800 md:bg-transparent border-r md:border-r-0 border-gray-700
        transform ${isMobileMenuOpen ? 'translate-x-0' : '-translate-x-full'} md:transform-none
        transition-transform duration-300 ease-in-out z-40 md:z-auto
        md:flex md:justify-center md:border-b md:border-gray-700 md:mb-6
      `}>
        <div className="p-4 md:p-0">
          {/* Logo/Title */}
          <div className="mb-8 md:hidden">
            <h1 className="text-xl font-bold text-white">Gym Tracker</h1>
          </div>

          {/* Navigation Items */}
          <div className="space-y-2 md:space-y-0 md:flex md:space-x-1">
            {navItems.map((item) => {
              const Icon = item.icon;
              return (
                <button
                  key={item.id}
                  onClick={() => {
                    setActiveTab(item.id);
                    setIsMobileMenuOpen(false);
                  }}
                  className={`
                    w-full md:w-auto flex items-center gap-3 px-4 py-3 md:py-2 rounded-lg
                    transition-colors duration-200
                    ${activeTab === item.id
                      ? 'bg-blue-600 text-white'
                      : 'text-gray-300 hover:bg-gray-700 hover:text-white'
                    }
                  `}
                >
                  <Icon size={20} />
                  <span className="md:hidden lg:inline">{item.label}</span>
                </button>
              );
            })}
          </div>
        </div>
      </nav>
    </>
  );
};

export default Navigation;
