import React, { createContext, useContext, useState, useEffect } from 'react';
import { ref, set, get } from 'firebase/database';
import { database } from '../firebase/config';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

// Demo users for testing
const DEMO_USERS = {
  'demo@gymtracker.com': {
    uid: 'demo-user-123',
    email: 'demo@gymtracker.com',
    displayName: 'Demo User',
    password: 'demo123456'
  }
};

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [userProfile, setUserProfile] = useState(null);

  // Check for existing session on load
  useEffect(() => {
    const savedUser = localStorage.getItem('gymtracker_user');
    if (savedUser) {
      const user = JSON.parse(savedUser);
      setCurrentUser(user);
      loadUserProfile(user.uid);
    } else {
      setLoading(false);
    }
  }, []);

  // Sign up function (demo version)
  const signup = async (email, password, displayName) => {
    try {
      // Check if user already exists
      if (DEMO_USERS[email]) {
        throw new Error('User already exists');
      }

      // Create new user
      const newUser = {
        uid: `user-${Date.now()}`,
        email: email,
        displayName: displayName,
        createdAt: Date.now()
      };

      // Create user profile in database
      const userProfileData = {
        uid: newUser.uid,
        email: newUser.email,
        displayName: displayName,
        createdAt: Date.now(),
        settings: {
          units: 'lbs',
          theme: 'dark',
          notifications: true,
          weeklyGoal: 4,
          privacy: 'private'
        },
        stats: {
          totalWorkouts: 0,
          totalVolume: 0,
          joinDate: Date.now()
        }
      };

      // Save to database
      await set(ref(database, `users/${newUser.uid}`), userProfileData);
      
      // Save to local storage
      localStorage.setItem('gymtracker_user', JSON.stringify(newUser));
      
      setCurrentUser(newUser);
      setUserProfile(userProfileData);
      
      return { user: newUser };
    } catch (error) {
      console.error('Signup error:', error);
      throw error;
    }
  };

  // Sign in function (demo version)
  const signin = async (email, password) => {
    try {
      // Check demo users first
      if (DEMO_USERS[email] && DEMO_USERS[email].password === password) {
        const user = DEMO_USERS[email];
        localStorage.setItem('gymtracker_user', JSON.stringify(user));
        setCurrentUser(user);
        await loadUserProfile(user.uid);
        return { user };
      }

      // For other users, check if they exist in our system
      // This is a simplified demo version
      throw new Error('Invalid credentials');
    } catch (error) {
      console.error('Signin error:', error);
      throw error;
    }
  };

  // Sign out function
  const logout = async () => {
    localStorage.removeItem('gymtracker_user');
    setCurrentUser(null);
    setUserProfile(null);
  };

  // Reset password (demo version)
  const resetPassword = async (email) => {
    // In demo mode, just show success message
    return Promise.resolve();
  };

  // Load user profile
  const loadUserProfile = async (uid) => {
    try {
      const userRef = ref(database, `users/${uid}`);
      const snapshot = await get(userRef);
      if (snapshot.exists()) {
        setUserProfile(snapshot.val());
      } else {
        // Create default profile if it doesn't exist
        const defaultProfile = {
          uid: uid,
          email: currentUser?.email || '',
          displayName: currentUser?.displayName || 'User',
          createdAt: Date.now(),
          settings: {
            units: 'lbs',
            theme: 'dark',
            notifications: true,
            weeklyGoal: 4,
            privacy: 'private'
          },
          stats: {
            totalWorkouts: 0,
            totalVolume: 0,
            joinDate: Date.now()
          }
        };
        await set(userRef, defaultProfile);
        setUserProfile(defaultProfile);
      }
      setLoading(false);
    } catch (error) {
      console.error('Error loading user profile:', error);
      setLoading(false);
    }
  };

  // Update user profile
  const updateUserProfile = async (updates) => {
    if (!currentUser) return;
    
    try {
      const userRef = ref(database, `users/${currentUser.uid}`);
      const updatedProfile = { ...userProfile, ...updates };
      await set(userRef, updatedProfile);
      setUserProfile(updatedProfile);
    } catch (error) {
      console.error('Error updating profile:', error);
      throw error;
    }
  };

  // Get user data path for database operations
  const getUserDataPath = (path) => {
    if (!currentUser) return null;
    return `users/${currentUser.uid}/${path}`;
  };

  const value = {
    currentUser,
    userProfile,
    signup,
    signin,
    logout,
    resetPassword,
    updateUserProfile,
    getUserDataPath,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
