import React, { useState, useEffect } from 'react';
import { ref, onValue } from 'firebase/database';
import { database } from '../firebase/config';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import { Line, Pie } from 'react-chartjs-2';
import { format } from 'date-fns';
import { calculateMuscleGroupVolumes } from '../utils/calculations';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const ProgressCharts = () => {
  const [workouts, setWorkouts] = useState([]);
  const [selectedExercise, setSelectedExercise] = useState('');
  const [timeRange, setTimeRange] = useState('30'); // days
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const workoutsRef = ref(database, 'workouts');
    const unsubscribe = onValue(workoutsRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const workoutsList = Object.keys(data).map(key => ({
          id: key,
          ...data[key]
        })).sort((a, b) => new Date(a.date) - new Date(b.date));
        setWorkouts(workoutsList);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  // Get unique exercises for filter
  const uniqueExercises = [...new Set(
    workouts.flatMap(workout => 
      workout.exercises?.map(ex => ex.name) || []
    )
  )].sort();

  // Filter workouts by time range
  const filteredWorkouts = workouts.filter(workout => {
    const workoutDate = new Date(workout.date);
    const cutoffDate = new Date();
    cutoffDate.setDate(cutoffDate.getDate() - parseInt(timeRange));
    return workoutDate >= cutoffDate;
  });

  // Prepare strength progression data
  const getStrengthData = () => {
    if (!selectedExercise) return { labels: [], datasets: [] };

    const exerciseData = [];
    filteredWorkouts.forEach(workout => {
      const exercise = workout.exercises?.find(ex => ex.name === selectedExercise);
      if (exercise) {
        exerciseData.push({
          date: workout.date,
          weight: exercise.weight,
          oneRepMax: exercise.oneRepMax,
          volume: exercise.volume
        });
      }
    });

    return {
      labels: exerciseData.map(data => format(new Date(data.date), 'MMM dd')),
      datasets: [
        {
          label: 'Weight (lbs)',
          data: exerciseData.map(data => data.weight),
          borderColor: 'rgb(59, 130, 246)',
          backgroundColor: 'rgba(59, 130, 246, 0.1)',
          tension: 0.1,
          yAxisID: 'y',
        },
        {
          label: 'Est. 1RM (lbs)',
          data: exerciseData.map(data => data.oneRepMax),
          borderColor: 'rgb(16, 185, 129)',
          backgroundColor: 'rgba(16, 185, 129, 0.1)',
          tension: 0.1,
          yAxisID: 'y',
        }
      ]
    };
  };

  // Prepare volume progression data
  const getVolumeData = () => {
    const volumeData = filteredWorkouts.map(workout => ({
      date: workout.date,
      volume: workout.totalVolume || 0
    }));

    return {
      labels: volumeData.map(data => format(new Date(data.date), 'MMM dd')),
      datasets: [
        {
          label: 'Total Volume',
          data: volumeData.map(data => data.volume),
          borderColor: 'rgb(245, 158, 11)',
          backgroundColor: 'rgba(245, 158, 11, 0.1)',
          tension: 0.1,
          fill: true,
        }
      ]
    };
  };

  // Prepare muscle group distribution data
  const getMuscleGroupData = () => {
    const muscleGroupTotals = {};
    
    filteredWorkouts.forEach(workout => {
      if (workout.exercises) {
        const volumes = calculateMuscleGroupVolumes(workout.exercises);
        Object.keys(volumes).forEach(group => {
          muscleGroupTotals[group] = (muscleGroupTotals[group] || 0) + volumes[group];
        });
      }
    });

    const colors = [
      '#3B82F6', '#10B981', '#F59E0B', '#EF4444', 
      '#8B5CF6', '#F97316', '#06B6D4', '#84CC16'
    ];

    return {
      labels: Object.keys(muscleGroupTotals),
      datasets: [
        {
          data: Object.values(muscleGroupTotals),
          backgroundColor: colors.slice(0, Object.keys(muscleGroupTotals).length),
          borderWidth: 2,
          borderColor: '#1F2937',
        }
      ]
    };
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
        labels: {
          color: '#F9FAFB'
        }
      },
      title: {
        display: true,
        color: '#F9FAFB'
      },
    },
    scales: {
      x: {
        ticks: { color: '#9CA3AF' },
        grid: { color: '#374151' }
      },
      y: {
        ticks: { color: '#9CA3AF' },
        grid: { color: '#374151' }
      }
    }
  };

  const pieOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom',
        labels: {
          color: '#F9FAFB',
          padding: 20
        }
      },
      title: {
        display: true,
        text: 'Muscle Group Volume Distribution',
        color: '#F9FAFB'
      },
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-lg">Loading charts...</div>
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto p-4">
      <h2 className="text-2xl font-bold mb-6 text-center">Progress Analytics</h2>
      
      {/* Filters */}
      <div className="bg-gray-800 rounded-lg p-4 mb-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium mb-2">Time Range</label>
            <select
              value={timeRange}
              onChange={(e) => setTimeRange(e.target.value)}
              className="w-full p-2 bg-gray-700 rounded border border-gray-600 focus:border-blue-500"
            >
              <option value="7">Last 7 days</option>
              <option value="30">Last 30 days</option>
              <option value="90">Last 3 months</option>
              <option value="365">Last year</option>
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium mb-2">Exercise (for strength chart)</label>
            <select
              value={selectedExercise}
              onChange={(e) => setSelectedExercise(e.target.value)}
              className="w-full p-2 bg-gray-700 rounded border border-gray-600 focus:border-blue-500"
            >
              <option value="">Select exercise</option>
              {uniqueExercises.map(exercise => (
                <option key={exercise} value={exercise}>{exercise}</option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* Charts Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Strength Progression */}
        <div className="bg-gray-800 rounded-lg p-4">
          <h3 className="text-lg font-semibold mb-4">Strength Progression</h3>
          <div className="chart-container">
            {selectedExercise ? (
              <Line data={getStrengthData()} options={{
                ...chartOptions,
                plugins: {
                  ...chartOptions.plugins,
                  title: {
                    ...chartOptions.plugins.title,
                    text: `${selectedExercise} Progress`
                  }
                }
              }} />
            ) : (
              <div className="flex items-center justify-center h-full text-gray-400">
                Select an exercise to view strength progression
              </div>
            )}
          </div>
        </div>

        {/* Volume Progression */}
        <div className="bg-gray-800 rounded-lg p-4">
          <h3 className="text-lg font-semibold mb-4">Volume Progression</h3>
          <div className="chart-container">
            <Line data={getVolumeData()} options={{
              ...chartOptions,
              plugins: {
                ...chartOptions.plugins,
                title: {
                  ...chartOptions.plugins.title,
                  text: 'Total Workout Volume'
                }
              }
            }} />
          </div>
        </div>

        {/* Muscle Group Distribution */}
        <div className="bg-gray-800 rounded-lg p-4 lg:col-span-2">
          <h3 className="text-lg font-semibold mb-4">Muscle Group Distribution</h3>
          <div className="chart-container">
            <Pie data={getMuscleGroupData()} options={pieOptions} />
          </div>
        </div>
      </div>

      {/* Summary Stats */}
      <div className="bg-gray-800 rounded-lg p-4 mt-6">
        <h3 className="text-lg font-semibold mb-4">Summary Statistics</h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
          <div>
            <div className="text-2xl font-bold text-blue-400">{filteredWorkouts.length}</div>
            <div className="text-sm text-gray-400">Workouts</div>
          </div>
          <div>
            <div className="text-2xl font-bold text-green-400">
              {filteredWorkouts.reduce((sum, w) => sum + (w.exercises?.length || 0), 0)}
            </div>
            <div className="text-sm text-gray-400">Total Exercises</div>
          </div>
          <div>
            <div className="text-2xl font-bold text-yellow-400">
              {Math.round(filteredWorkouts.reduce((sum, w) => sum + (w.totalVolume || 0), 0))}
            </div>
            <div className="text-sm text-gray-400">Total Volume</div>
          </div>
          <div>
            <div className="text-2xl font-bold text-purple-400">
              {filteredWorkouts.length > 0 ? Math.round(
                filteredWorkouts.reduce((sum, w) => sum + (w.totalVolume || 0), 0) / filteredWorkouts.length
              ) : 0}
            </div>
            <div className="text-sm text-gray-400">Avg Volume/Workout</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProgressCharts;
