import React, { useState } from 'react';
import { ref, push, set } from 'firebase/database';
import { database } from '../firebase/config';
import { Plus, Save, X } from 'lucide-react';
import { calculateVolume, calculateOneRepMax } from '../utils/calculations';

const MUSCLE_GROUPS = [
  'Chest', 'Back', 'Shoulders', 'Arms', 'Legs', 'Core', 'Cardio'
];

const COMMON_EXERCISES = {
  'Chest': ['Bench Press', 'Incline Press', 'Dumbbell Press', 'Push-ups'],
  'Back': ['Deadlift', 'Pull-ups', 'Rows', 'Lat Pulldown'],
  'Shoulders': ['Overhead Press', 'Lateral Raises', 'Rear Delt Fly'],
  'Arms': ['Bicep Curls', 'Tricep Dips', 'Close Grip Press'],
  'Legs': ['Squat', 'Leg Press', 'Lunges', 'Calf Raises'],
  'Core': ['Plank', 'Crunches', 'Russian Twists'],
  'Cardio': ['Treadmill', 'Cycling', 'Rowing']
};

const WorkoutLogger = () => {
  const [workout, setWorkout] = useState({
    name: '',
    date: new Date().toISOString().split('T')[0],
    exercises: []
  });
  
  const [currentExercise, setCurrentExercise] = useState({
    name: '',
    muscleGroup: '',
    sets: 1,
    reps: 1,
    weight: 0
  });

  const addExercise = () => {
    if (!currentExercise.name || !currentExercise.muscleGroup) return;
    
    const exercise = {
      ...currentExercise,
      volume: calculateVolume(currentExercise.sets, currentExercise.reps, currentExercise.weight),
      oneRepMax: calculateOneRepMax(currentExercise.weight, currentExercise.reps)
    };
    
    setWorkout(prev => ({
      ...prev,
      exercises: [...prev.exercises, exercise]
    }));
    
    setCurrentExercise({
      name: '',
      muscleGroup: '',
      sets: 1,
      reps: 1,
      weight: 0
    });
  };

  const removeExercise = (index) => {
    setWorkout(prev => ({
      ...prev,
      exercises: prev.exercises.filter((_, i) => i !== index)
    }));
  };

  const saveWorkout = async () => {
    if (!workout.name || workout.exercises.length === 0) return;
    
    try {
      const workoutRef = ref(database, 'workouts');
      const newWorkoutRef = push(workoutRef);
      await set(newWorkoutRef, {
        ...workout,
        timestamp: Date.now(),
        totalVolume: workout.exercises.reduce((sum, ex) => sum + ex.volume, 0)
      });
      
      // Reset form
      setWorkout({
        name: '',
        date: new Date().toISOString().split('T')[0],
        exercises: []
      });
      
      alert('Workout saved successfully!');
    } catch (error) {
      console.error('Error saving workout:', error);
      alert('Error saving workout');
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <h2 className="text-2xl font-bold mb-6 text-center">Log Workout</h2>
      
      {/* Workout Details */}
      <div className="bg-gray-800 rounded-lg p-4 mb-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium mb-2">Workout Name</label>
            <input
              type="text"
              value={workout.name}
              onChange={(e) => setWorkout(prev => ({ ...prev, name: e.target.value }))}
              className="w-full p-2 bg-gray-700 rounded border border-gray-600 focus:border-blue-500"
              placeholder="e.g., Push Day"
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-2">Date</label>
            <input
              type="date"
              value={workout.date}
              onChange={(e) => setWorkout(prev => ({ ...prev, date: e.target.value }))}
              className="w-full p-2 bg-gray-700 rounded border border-gray-600 focus:border-blue-500"
            />
          </div>
        </div>
      </div>

      {/* Add Exercise Form */}
      <div className="bg-gray-800 rounded-lg p-4 mb-6">
        <h3 className="text-lg font-semibold mb-4">Add Exercise</h3>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <div>
            <label className="block text-sm font-medium mb-2">Muscle Group</label>
            <select
              value={currentExercise.muscleGroup}
              onChange={(e) => setCurrentExercise(prev => ({ ...prev, muscleGroup: e.target.value, name: '' }))}
              className="w-full p-2 bg-gray-700 rounded border border-gray-600 focus:border-blue-500"
            >
              <option value="">Select muscle group</option>
              {MUSCLE_GROUPS.map(group => (
                <option key={group} value={group}>{group}</option>
              ))}
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium mb-2">Exercise</label>
            <select
              value={currentExercise.name}
              onChange={(e) => setCurrentExercise(prev => ({ ...prev, name: e.target.value }))}
              className="w-full p-2 bg-gray-700 rounded border border-gray-600 focus:border-blue-500"
              disabled={!currentExercise.muscleGroup}
            >
              <option value="">Select exercise</option>
              {currentExercise.muscleGroup && COMMON_EXERCISES[currentExercise.muscleGroup]?.map(exercise => (
                <option key={exercise} value={exercise}>{exercise}</option>
              ))}
            </select>
          </div>
        </div>

        <div className="grid grid-cols-3 gap-4 mb-4">
          <div>
            <label className="block text-sm font-medium mb-2">Sets</label>
            <input
              type="number"
              min="1"
              value={currentExercise.sets}
              onChange={(e) => setCurrentExercise(prev => ({ ...prev, sets: parseInt(e.target.value) || 1 }))}
              className="w-full p-2 bg-gray-700 rounded border border-gray-600 focus:border-blue-500"
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-2">Reps</label>
            <input
              type="number"
              min="1"
              value={currentExercise.reps}
              onChange={(e) => setCurrentExercise(prev => ({ ...prev, reps: parseInt(e.target.value) || 1 }))}
              className="w-full p-2 bg-gray-700 rounded border border-gray-600 focus:border-blue-500"
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-2">Weight (lbs)</label>
            <input
              type="number"
              min="0"
              step="0.5"
              value={currentExercise.weight}
              onChange={(e) => setCurrentExercise(prev => ({ ...prev, weight: parseFloat(e.target.value) || 0 }))}
              className="w-full p-2 bg-gray-700 rounded border border-gray-600 focus:border-blue-500"
            />
          </div>
        </div>

        <button
          onClick={addExercise}
          className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded flex items-center justify-center gap-2"
        >
          <Plus size={20} />
          Add Exercise
        </button>
      </div>

      {/* Exercise List */}
      {workout.exercises.length > 0 && (
        <div className="bg-gray-800 rounded-lg p-4 mb-6">
          <h3 className="text-lg font-semibold mb-4">Exercises ({workout.exercises.length})</h3>
          <div className="space-y-3">
            {workout.exercises.map((exercise, index) => (
              <div key={index} className="bg-gray-700 rounded p-3 flex justify-between items-center">
                <div>
                  <div className="font-medium">{exercise.name}</div>
                  <div className="text-sm text-gray-300">
                    {exercise.sets} sets Ã— {exercise.reps} reps @ {exercise.weight} lbs
                  </div>
                  <div className="text-xs text-gray-400">
                    Volume: {exercise.volume} | Est. 1RM: {exercise.oneRepMax} lbs
                  </div>
                </div>
                <button
                  onClick={() => removeExercise(index)}
                  className="text-red-400 hover:text-red-300"
                >
                  <X size={20} />
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Save Button */}
      <button
        onClick={saveWorkout}
        disabled={!workout.name || workout.exercises.length === 0}
        className="w-full bg-green-600 hover:bg-green-700 disabled:bg-gray-600 disabled:cursor-not-allowed text-white py-3 px-4 rounded flex items-center justify-center gap-2 text-lg font-medium"
      >
        <Save size={20} />
        Save Workout
      </button>
    </div>
  );
};

export default WorkoutLogger;
