import React, { useState } from 'react';
import Navigation from './components/Navigation';
import WorkoutLogger from './components/WorkoutLogger';
import ProgressCharts from './components/ProgressCharts';
import BodyMetrics from './components/BodyMetrics';
import PersonalRecords from './components/PersonalRecords';
import './index.css';

function App() {
  const [activeTab, setActiveTab] = useState('logger');
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const renderActiveComponent = () => {
    switch (activeTab) {
      case 'logger':
        return <WorkoutLogger />;
      case 'charts':
        return <ProgressCharts />;
      case 'metrics':
        return <BodyMetrics />;
      case 'records':
        return <PersonalRecords />;
      default:
        return <WorkoutLogger />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {/* Header */}
      <header className="bg-gray-800 border-b border-gray-700 p-4">
        <div className="max-w-6xl mx-auto">
          <h1 className="text-2xl font-bold text-center md:text-left">
            ðŸ’ª Gym Progress Tracker
          </h1>
        </div>
      </header>

      <div className="flex">
        {/* Navigation */}
        <Navigation 
          activeTab={activeTab}
          setActiveTab={setActiveTab}
          isMobileMenuOpen={isMobileMenuOpen}
          setIsMobileMenuOpen={setIsMobileMenuOpen}
        />

        {/* Main Content */}
        <main className="flex-1 md:ml-0 p-4 md:p-6">
          {renderActiveComponent()}
        </main>
      </div>

      {/* Footer */}
      <footer className="bg-gray-800 border-t border-gray-700 p-4 text-center text-gray-400 text-sm">
        <p>Track your progress, achieve your goals ðŸŽ¯</p>
      </footer>
    </div>
  );
}

export default App;
